#pragma once#include "CmdNo.h"#include <MacTypes.h>class MenuBase {	public:	virtual Boolean	DoCommand(long cmd) = 0;	virtual void	GetCommandStatus(long cmd, Boolean &enabled, short &markChar, Str255 &name) = 0;};void	DoCommand(long cmd);						// globalen Menüpunkt angewähltvoid	GetCommandStatus(long cmd, Boolean &enabled, short &markChar, Str255 &name);	// Command-Status ermittelnvoid		MenuBarInit(short MenuId);					// Menüleiste initialisierenvoid		EventMenu(long m, MenuBase *mc);Boolean		IsCommand(long cmd, MenuBase *mc);void		AdjustMenu(MenuBase *mc);void		SendMenuCmd(long cmd, MenuBase *mc);