/*** *	LongControl.h * *	Hier sind sämtliche ControlManager-Funktionen definiert, auch, die, die _nicht_ verändert *	wurden. Man kann bei Slidern also _stets_ das “o” vor den Control-Manager-Funktionen schreiben * *	©1992 ∑-Soft, Markus Fritze ***/#pragma once#include "GlobalDefines.h"#include <Controls.h>ControlHandle	oNewControl(WindowPtr w,Rect *r,Str255 title,Boolean visible,long value,long min,long max,short ctrlType,long refCon);void			oDisposeControl(ControlHandle c);void			oKillControls(WindowPtr w);long			oGetControlMinimum(ControlHandle c);void			oSetControlMinimum(ControlHandle c,long value);long			oGetControlMaximum(ControlHandle c);void			oSetControlMaximum(ControlHandle c,long value);long			oGetControlValue(ControlHandle c);void			oSetControlValue(ControlHandle c,long value);long			oGetControlReference(ControlHandle c);void			oSetControlReference(ControlHandle c,long value);short			oFindControl(Point localPt,WindowPtr w,ControlHandle *c);short			oTestControl(ControlHandle c,Point localPt);long			ThumbPosCalc(ControlHandle c,short val);long			TrackThumb(Point thePoint,ControlHandle c,ControlActionUPP proc);#define	oGetControlAction(theControl) GetControlAction(theControl)#define oSetControlAction(theControl,actionProc) SetControlAction(theControl,actionProc)#define oShowControl(theControl) ShowControl(theControl)#define oHideControl(theControl) HideControl(theControl)#define oDraw1Control(theControl) Draw1Control(theControl)#define oHiliteControl(theControl,hiliteCode) HiliteControl(theControl,hiliteCode)#define oDrawControls(theWindow) DrawControls(theWindow)#define oUpdateControls(theWindow,theRgn) UpdateControls(theWindow,theRgn)#define oGetControlTitle(theControl,title) GetControlTitle(theControl,title)#define oSetControlTitle(theControl,title) SetControlTitle(theControl,title)#define oTrackControl(theControl,localPt,actionProc) TrackControl(theControl,localPt,actionProc)#define oMoveControl(theControl,toHoriz,toVert) MoveControl(theControl,toHoriz,toVert)#define oSizeControl(theControl,wide,high) SizeControl(theControl,wide,high)#define oDragControl(theControl,startPt,limitRect,slopRect,axis) DragControl(theControl,startPt,limitRect,slopRect,axis)