/*** *	Selektions-Fenster ***/#pragma once#include "WindowBase.h"#include "Preferences.h"#define ToolAnz		7#define ToolBaseId	1000// Preferences "Tool", Id 1#define kToolbarPrefType	'Tool'#define kToolbarPrefId		1#if defined(powerc) || defined (__powerc)#pragma options align=mac68k#endiftypedef struct {	char		openNotAtStart;		// true, wenn Toolbar _nicht_ beim Start šffnen	char		toolbarHorizontal;	// true, wenn horizontale Toolbar	Point		position;			// Position der Toolbar auf dem Screen} ToolbarPref;#if defined(powerc) || defined(__powerc)#pragma options align=reset#endifclass MCToolbar : public WindowBase {	ToolbarPref	pref;	void		ToolPlotIcon(short id, short resid, short select);	void		GetIconRect(short id, Rect &theRect);	void		RedrawIcon(short id);public:	virtual				~MCToolbar();	virtual void		Open(void);	virtual void		Klick(Point p, short clicks, Boolean &onItem);	// Klick ins Window, ggf. Mehrfachklick	virtual void		Draw(void);						// Window updaten	virtual void		EventZoom(short partCode);	virtual void		WindowMoved(Point p);	void				Update(void);	void				Button(long cmd);	virtual void		MausMoved(Point thePoint, RgnHandle *mouseRgn);};extern MCToolbar	*WWToolbar;