/*** *	Headsheet ***/#pragma once#include "WindowScroll.h"#include <QDOffscreen.h>class MCView : public WindowScroll {	long		ModellId;		// welches Modell?	short		Pict;			// angezeigtes Bild vom Modell	Rect		PictRect;		// aktuelle Bildgrš§e	Rect		PictDefaultRect;// Default-Bildgrš§e	short		Zoom;			// Zoom-Modus (>0 => Vergrš§ern, <0 => Verkleinern, =0 => Normalbild)	GWorldPtr	PictGWorld;		// evtl. GWorld	void		SetTitle(void);	void		WindToZoom(void);	void		SetupGWorld(void);	void		CenterDrawPict(short pict, Rect &r, Boolean draw);public:	virtual				~MCView();	virtual void		Open(long mid, short pic);	virtual void		Draw(long xOffset, long yOffset, Rect visRect);					// Window updaten	virtual void		Klick(Point p, short clicks, Boolean &onItem);	// Klick ins Window, ggf. Mehrfachklick	virtual Boolean		DoCommand(long cmd);	virtual void		GetCommandStatus(long cmd, Boolean &enabled, short &markChar, Str255 &name);	virtual void		MausMoved(Point where, RgnHandle *mouseRgn);	// Mauszeiger anpassen};