/*** *	Window-Fenster mit zwei Scrollbars ***/#pragma once#include "WindowBase.h"#include "LongControl.h"class WindowScroll : protected WindowBase {	virtual void	Draw(void);						// Window updaten (nicht Ÿberladbar!)protected:	ControlHandle	hScroller;	// horizontaler Scrollbar	ControlHandle	vScroller;	// vertikaler Scrollbar	long			viewWidth;	long			viewHeight;	long			viewXScroll;	long			viewYScroll;	void			SetDeltaSlider(long xd, long yd);	static pascal void	ScrollProc(ControlHandle c, short thePart);public:	virtual void	Open(Rect &r, StringPtr theName, long width, long height, long xscroll, long yscroll);	virtual			~WindowScroll();	virtual void	Draw(long xOffset, long yOffset, Rect visRect);	virtual void	EventActivate(Boolean flag);	// Window wurde aktiviert	virtual void	Adjust(void);					// neue Fenstergrš§e wurde gesetzt	virtual void	Klick(Point p, short clicks, Boolean &onItem);	// Klick ins Window, ggf. Mehrfachklick	virtual void	EventZoom(short partCode);		// der Zoom mu§ anders verwaltet werden	virtual void	EventGrow(void);				// der Grow ebenfalls selbst verwaltet werden!	virtual void	GetVisRect(Rect &r);};