/*** *	Diese kleine Klasse dient für den einfachen Zugriff auf dBASE Dateien * *	©1994 ∑-Soft, Markus Fritze ***/#pragma oncetypedef unsigned char	*UnsPtr;class dBASEFile {private:	UnsPtr				Dbp;				// Ptr auf die Datenbank (steht komplett im RAM)	unsigned short	FieldHdrSize;		// Länge eines Feldeintrages im Header (16 oder 32, je nach Version)	unsigned short	DataSize;			// Länge eines Datensatzes	unsigned long		Danz;				// Anzahl der Datensätze	UnsPtr				HeaderPtr;			// Ptr auf den ersten Feldnamen im Header	UnsPtr				DataSetPtr;			// Ptr auf den ersten Datensatz	short				FieldLenOffset;		// Offset auf die Feldlänge im Header (12 oder 16, je nach Version)	void			ConvertFieldname(Ptr name);	short			GetFieldOffset(Ptr name, short *flen);public:	void			Init(UnsPtr dbp);		// Database setzen	inline unsigned long	Count(void) { return Danz; };	// Anzahl der Datensätze ermitteln	UnsPtr			Get(unsigned long no, Ptr fieldname);	// ein Feld eines Datensatzes auslesen	UnsPtr			GetInd(unsigned long no, short strind, short id);	unsigned long	GetBitindex(unsigned long no,short strind, const int id, const int strListId);	int				GetIndex(unsigned long no,short strind, const int id, const int strListId);};