/*** *	Datenbank-Format auf der Festplatte und im RAM von mastercast. * *	(c)1994 Dekotec Abt. Info-Sys, Markus Fritze ***/#pragma once#include "GlobalDefines.h"#include "List.h"#include "dBASE Import.h"// Diese Struktur beschreibt einen String, von denen, die dem Programm bekannt sind.class AgenturList;class Agentur {	friend class	AgenturList;	long		Id;				// ewig eindeutige Agentur-Id	char		Name[60];		// Name der Agentur	char		Strasse[80];	// Anschrift der Agentur	char		Ort[80];	char		Telefon[50];	// Telefonnummer	char		Fax[50];		// Faxnummer der Agenturpublic:	long		GetId(void) { return Id; };	char		*GetName(void) { return Name; };	char		*GetStrasse(void) { return Strasse; };	char		*GetOrt(void) { return Ort; };	char		*GetTelefon(void) { return Telefon; };	char		*GetFax(void) { return Fax; };};class AgenturList : public TList {public:	Ptr			EnterAgenturDatensatz(dBASEFile *db, long id);	void		Add(Agentur* agentur);	void		Remove(Agentur* agentur);	Agentur		*FindId(long id);};extern AgenturList	*AList;// Beschreibung eines Modells.typedef struct modell {	long		Id;				// ewig eindeutige Id des Modells	long		Agentur;		// Agentur, die dieses Modell vertritt	long		Look;			// Bitfeld LookTyp	long		Eignung;		// Bitfeld EignungTyp	long		Talente;		// Bitfeld TalenteTyp	long		Fuehrerschein;	// Bitfeld FuehrerschTyp	long		Sportarten;		// Bitfeld SportTyp	long		Besonders;		// Bitfeld BesondTyp	long		Sprachen;		// Bitfeld SprachenTyp//	long		Bh;				// (60...99, A,B,C,D,DD) - 4 Zeichen ASCII ggf. mit Nullbyte dran	short		GebJahr;		// z.B. 1967	short		Groesse;		// Angabe in cm (100...299)	short		Brust;			// Angabe in cm (0...199)	short		Taille;			// Angabe in cm (0...199)	short		Huefte;			// Angabe in cm (0...199)	short		Konfektion;		// (0...199)	short		Kragen;			// Angabe in cm (30...49)	short		Schuhe;			// Angabe in cm (20...59)	short		Hut;			// Angabe in cm (50...69)	short		Ring;			// Angabe in mm (50...69)	char		Modellname[32];	// Name vom Modell in der Agentur, etc. (momentane max.Len = 21 (+ Nullbyte))//	char		Vorname[60];	// Vorname (erstmal nur intern)//	char		Nachname[60];	// Nachname (erstmal nur intern)	char		PicAnz;			// Anzahl der Bilder	char		Geschlecht;		// 1:Frau, 2:Mann, 3:Kind, 4:... (evtl. Tiere, oder so)	char		Base;			// Base (Deutschland, Europa, USA)	char		Haarfarbe;		// enum HaarfarbeTyp	char		Haartyp;		// enum HaartypTyp	char		Haare;			// enum HaarTyp	char		Augenfarbe;		// enum AugenfarbeTyp} ModellStruct;#if !DEMOextern ModellStruct		**ModellList;#else#define MAXMODELLANZ	50extern ModellStruct		*ModellList[MAXMODELLANZ + 1];#endifextern short			ModellAnz;void			DB_MInit(short count);void			DB_FreeAll(void);void			DB_MInsert(ModellStruct *ms);void			DB_MSort(void);ModellStruct	*DB_MGet(long mid);ModellStruct	**DB_MFound(char *s, ModellStruct **ml);ModellStruct	**DB_MFoundFirst(char *s);ModellStruct	**DB_MFoundNext(char *s, ModellStruct **ml);